window.addEventListener('scroll', function() {
	var element = document.getElementById("scroll-mark");
	var position = element.getBoundingClientRect();

	// checking whether fully visible
	if(position.top >= 0 && position.bottom <= window.innerHeight) {
		if (!(arrow.classList.contains('-hidden'))) {
		arrow.classList.toggle('-hidden');
		dot1.classList.toggle('-h1');
	  dot2.classList.toggle('-h2');
	  dot3.classList.toggle('-h3');
	  dot4.classList.toggle('-h4');
	  dot5.classList.toggle('-h5');
	  dot6.classList.toggle('-h6');
	  dot7.classList.toggle('-h7');
	  dot8.classList.toggle('-h8');
	  dot9.classList.toggle('-h9');
	  dot10.classList.toggle('-h10');
		}
	} else {
		if (arrow.classList.contains('-hidden')) {
        		arrow.classList.toggle('-hidden');
			dot1.classList.toggle('-h1');
		  dot2.classList.toggle('-h2');
		  dot3.classList.toggle('-h3');
		  dot4.classList.toggle('-h4');
		  dot5.classList.toggle('-h5');
		  dot6.classList.toggle('-h6');
		  dot7.classList.toggle('-h7');
		  dot8.classList.toggle('-h8');
		  dot9.classList.toggle('-h9');
		  dot10.classList.toggle('-h10');
		}
    }
});

